function loadOrganisms(){var a=$("#marine-organisms");var d=$("#animal-organisms");var b=$("#plant-organisms");var c=$("#microbial-organisms");$.ajax({url:"SelectAllOrganismByWorld",type:"GET",dataType:"json",cache:false,async:true}).done(function(e){e.forEach(function(f){if(f.world.worldName.toLowerCase().indexOf("plant")>-1){b.append('<a href="organism?id='+f.organismId+'"><div class="col-lg-3 col-md-3 col-sm-3 col-xs-12 organisms-list organisms-plant-world"><li>'+f.commonName+"</li></div></a>")}else{if(f.world.worldName.toLowerCase().indexOf("animal")>-1){d.append('<a href="organism?id='+f.organismId+'"><div class="col-lg-3 col-md-3 col-sm-3 col-xs-12 organisms-list organisms-animal-world"><li>'+f.commonName+"</li></div></a>")}else{if(f.world.worldName.toLowerCase().indexOf("marine")>-1){a.append('<a href="organism?id='+f.organismId+'"><div class="col-lg-3 col-md-3 col-sm-3 col-xs-12 organisms-list organisms-marine-world"><li>'+f.commonName+"</li></div></a>")}else{if(f.world.worldName.toLowerCase().indexOf("microbial")>-1){c.append('<a href="organism?id='+f.organismId+'"><div class="col-lg-3 col-md-3 col-sm-3 col-xs-12 organisms-list organisms-microbial-world"><li>'+f.commonName+"</li></div></a>")}else{b.append("<li>No organisms yet</li>");d.append("<li>No organisms yet</li>");a.append("<li>No organisms yet</li>");c.append("<li>No organisms yet</li>")}}}}})})}$(document).ready(function(){$("#form-search-organism").submit(function(b){$("#search-result").html("");var a=$("#keyword").val();if(a.length>=3){$.ajax({url:"SearchOrganism",type:"GET",dataType:"json",cache:false,async:true,data:$("#form-search-organism").serialize()}).done(function(c){document.getElementById("search-result").style.display="block";if(c.length===0){$("#search-result").append("<p>Organism not found</p>")}c.forEach(function(d){$("#search-result").append('<p><a href="organism?id='+d.organismId+'" >'+d.commonName+"</a></p>")});setTimeout(function(){$("#search-result").html("");$("#form-search-organism")[0].reset()},15000)})}else{document.getElementById("search-result").style.display="block";$("#search-result").append("<p>More characters required</p>")}b.preventDefault()});$("#create-subscriber-form").submit(function(b){if($("#create-subscriber-form").find(".has-error").length){return}var a=$("#create-subscriber-message");a.show();$.ajax({url:"InsertSubscriber",type:"POST",dataType:"text",data:$("#create-subscriber-form").serialize()}).done(function(c){if(c==="succes"){a.append('<div class="alert alert-success" role="alert"><a href="#" class="close" data-dismiss="alert">&times;</a>Subscribed succesfull</div>')}else{if(c==="exists"){a.append('<div class="alert alert-danger" role="alert"><a href="#" class="close" data-dismiss="alert">&times;</a>Email already exists</div>')}else{if(c==="sql"){a.append('<div class="alert alert-danger" role="alert"><a href="#" class="close" data-dismiss="alert">&times;</a>Service unavailable!</div>')}}}setTimeout(function(){a.fadeOut("slow");a.empty()},2800);$("#create-subscriber-form")[0].reset()});b.preventDefault()})});